import React, { useState, useRef, useEffect } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";

const QuizCreator = () => {
  const [title, setTitle] = useState('');
  const [quizType, setQuizType] = useState('multiple-choice');
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [questions, setQuestions] = useState([]);

  const textAreaRef = useRef();

  useEffect(() => {
    if (textAreaRef.current) {
      textAreaRef.current.style.height = 'auto';
      textAreaRef.current.style.height = `${textAreaRef.current.scrollHeight}px`;
    }
  }, [question]);

  const handleAddQuestion = () => {
    // Create a new question object and add it to the questions array
    const newQuestion = {
      questionText: question,
      answerOptions: [
        { answerText: answer, isCorrect: true }, // You can modify this to include multiple answer options
      ],
    };
    setQuestions([...questions, newQuestion]);
    setQuestion('');
    setAnswer('');
  };

  const handleAddAnswerOption = () => {
    // Create a new answer option and add it to the current question's answerOptions array
    const newAnswerOption = { answerText: answer, isCorrect: false };
    const updatedQuestions = questions.map((q, index) => {
      if (index === questions.length - 1) {
        return {
          ...q,
          answerOptions: [...q.answerOptions, newAnswerOption],
        };
      }
      return q;
    });
    setQuestions(updatedQuestions);
    setAnswer('');
  };

  return (
    <div className="container">
      <h1>Quiz Creator</h1>
      <div className="mb-3">
        <label className="form-label">Quiz Title:</label>
        <input
          type="text"
          className="form-control"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <label className="form-label">Quiz Type:</label>
        <select
          className="form-select"
          value={quizType}
          onChange={(e) => setQuizType(e.target.value)}
        >
          <option value="multiple-choice">Multiple Choice</option>
          <option value="true-false">True/False</option>
          <option value="fill-in-the-blank">Fill in the Blank</option>
        </select>
      </div>
      <div className="mb-3">
        <label className="form-label">Question:</label>
        <textarea
          ref={textAreaRef}
          className="form-control"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
        />
      </div>
      {quizType === 'multiple-choice' && (
        <div className="mb-3">
          <label className="form-label">Answer:</label>
          <input
            type="text"
            className="form-control"
            value={answer}
            onChange={(e) => setAnswer(e.target.value)}
          />
          <button
            className="btn btn-secondary mt-2"
            onClick={handleAddAnswerOption}
          >
            Add Answer Option
          </button>
        </div>
      )}
      <button className="btn btn-primary" onClick={handleAddQuestion}>
        Add Question
      </button>
      <div>
        {questions.map((q, index) => (
          <div key={index} className="mt-3">
            <h4>Question {index + 1}</h4>
            <p>{q.questionText}</p>
            <div>
              {q.answerOptions.map((option, idx) => (
                <div key={idx}>
                  {quizType === 'multiple-choice' ? (
                    <>
                      <input
                        type="checkbox"
                        checked={option.isCorrect}
                        disabled
                      />
                      <label>{option.answerText}</label>
                    </>
                  ) : (
                    <input type="radio" disabled />
                  )}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default QuizCreator;
