import React, { useState } from "react";
import { Button, Form, FormGroup, Label, Input } from "reactstrap";

const QuizCreationPage = () => {
  const [quiz, setQuiz] = useState({
    title: "",
    questions: [],
    answers: [],
    quizType: "multiple-choice",
  });

  const handleTitleChange = (e) => {
    setQuiz((prevQuiz) => ({ ...prevQuiz, title: e.target.value }));
  };

  const handleQuestionChange = (index, e) => {
    const newQuestions = [...quiz.questions];
    newQuestions[index] = e.target.value;
    setQuiz((prevQuiz) => ({ ...prevQuiz, questions: newQuestions }));
  };

  const handleAnswerChange = (index, e) => {
    const newAnswers = [...quiz.answers];
    newAnswers[index] = e.target.value;
    setQuiz((prevQuiz) => ({ ...prevQuiz, answers: newAnswers }));
  };

  const handleAddQuestion = () => {
    setQuiz((prevQuiz) => ({
      ...prevQuiz,
      questions: [...prevQuiz.questions, ""],
      answers: [...prevQuiz.answers, ""],
    }));
  };

  const handleQuizTypeChange = (e) => {
    setQuiz((prevQuiz) => ({ ...prevQuiz, quizType: e.target.value }));
  };

  const handleInputResize = (e) => {
    e.target.setAttribute("size", e.target.value.length);
  };

  const handleQuizSubmit = (e) => {
    e.preventDefault();
    console.log(quiz);
    // Here, you can send the quiz data to your backend or do further processing
  };

  return (
    <div className="container mt-5">
      <h2>Create a New Quiz</h2>
      <Form onSubmit={handleQuizSubmit}>
        <FormGroup>
          <Label for="quizTitle">Quiz Title:</Label>
          <Input
            type="text"
            id="quizTitle"
            value={quiz.title}
            onChange={handleTitleChange}
          />
        </FormGroup>
        <FormGroup>
          <Label for="quizType">Select Quiz Type:</Label>
          <Input
            type="select"
            id="quizType"
            value={quiz.quizType}
            onChange={handleQuizTypeChange}
          >
            <option value="multiple-choice">Multiple Choice</option>
            <option value="true-false">True/False</option>
            <option value="fill-in-the-blank">Fill in the Blank</option>
          </Input>
        </FormGroup>
        {quiz.questions.map((question, index) => (
          <FormGroup key={index}>
            <Label for={`question${index + 1}`}>Question {index + 1}:</Label>
            <Input
              type="text"
              id={`question${index + 1}`}
              value={question}
              onChange={(e) => handleQuestionChange(index, e)}
              onInput={handleInputResize} // Add onInput event to dynamically resize the input field
            />
            <Label for={`answer${index + 1}`}>Answer:</Label>
            <Input
              type="text"
              id={`answer${index + 1}`}
              value={quiz.answers[index] || ""}
              onChange={(e) => handleAnswerChange(index, e)}
            />
          </FormGroup>
        ))}
        <Button color="primary" onClick={handleAddQuestion}>
          Add Question
        </Button>{" "}
        <Button type="submit" color="success">
          Create Quiz
        </Button>
      </Form>
    </div>
  );
};

export default QuizCreationPage;
